CREATE TABLE IF NOT EXISTS `lsn-voltage` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `owner` varchar(255) NOT NULL DEFAULT '0',
  `energy_amount` int(100) NOT NULL DEFAULT 0,
  `durability` float NOT NULL DEFAULT 100,
  `upgrades` longtext CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NOT NULL DEFAULT '{"durability": 0, "efficiency": 0, "max_capacity": 0, "grid_connection": 0, "security": 0}' CHECK (json_valid(`upgrades`)),
  `coords` tinytext NOT NULL DEFAULT '0',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=70 DEFAULT CHARSET=utf8mb3 COLLATE=utf8mb3_general_ci;

